#
# View "v_hadir"
#

DROP VIEW IF EXISTS `v_hadir`;
CREATE
  ALGORITHM = UNDEFINED
  VIEW `v_hadir`
  AS
  SELECT
    `orang`.`nama`, `hadir`.`tanggal`
  FROM
    (`orang`
      JOIN `hadir`)
  WHERE
    `orang`.`id` = `hadir`.`id_orang`
  GROUP BY
    `hadir`.`id`;

#
# View "v_konfirmasi"
#

DROP VIEW IF EXISTS `v_konfirmasi`;
CREATE
  ALGORITHM = UNDEFINED
  VIEW `v_konfirmasi`
  AS
  SELECT
    `id`, `nama`, `konfirmasi2`
  FROM
    `orang`;

#
# View "v_rekapkonfirmasi"
#

DROP VIEW IF EXISTS `v_rekapkonfirmasi`;
CREATE
  ALGORITHM = UNDEFINED
  VIEW `v_rekapkonfirmasi`
  AS
  SELECT
    (SELECT COUNT(0) FROM `orang` WHERE `konfirmasi2` = 'Hadir') AS 'Hadir', (SELECT COUNT(0) FROM `orang` WHERE `konfirmasi2` = 'Tidak Bisa') AS 'Tidak_Bisa', (SELECT COUNT(0) FROM `orang` WHERE `konfirmasi2` = 'Masih Dalam Pertimbangan') AS 'Masih_Pertimbangan';
